export async function getStaticPaths() {
  const cmsurl = "https://myblog-nextjs.microcms.io/api/v1/blog";

  const res = await fetch(cmsurl, {
    method: "GET",
    headers: {
      "X-API-KEY": "c2bf30d3-1da2-45a3-8327-b992b9006af3",
    },
  });
  const posts = await res.json();

  const articleList = posts.contents.reverse();

  const paths = articleList.map(
    (article: PostType) => `/posts/${article.id.toString}`
  );

  return {
    paths,
    fallback: true,
  };
}

export async function getStaticProps({ params }: Params) {
  const id = params;

  const cmsurl = `https://myblog-nextjs.microcms.io/api/v1/blog/${id}`;

  const res = await fetch(cmsurl, {
    method: "GET",
    headers: {
      "X-API-KEY": "c2bf30d3-1da2-45a3-8327-b992b9006af3",
    },
  });
  const posts = await res.json();
  const article = posts.contents;

  return {
    props: {
      article,
    },
  };
}
